import java.awt.*;public class Rectangle {    int x;    int y;    int w;    int h;    public Rectangle(int x, int y, int w, int h) {        this.x = x;        this.y = y;        this.h = h;        this.w = w;    }    public boolean contains(Point p) {        return (p.x > this.x - this.w &&                p.x < this.x + this.w &&                p.y > this.y - this.h &&                p.y < this.y + this.h);    }}