import org.w3c.dom.css.Rect;import java.awt.*;import java.util.Random;import javax.swing.*;public class Visualize extends JFrame{    static Rectangle boundary = new Rectangle(200, 200,200,200);    static QuadTree qt = new QuadTree(boundary, 4);    public static void main(String[] args) throws Exception {        SwingUtilities.invokeLater(new Runnable() {            @Override            public void run() {                new Visualize().setVisible(true);            }        });    }    public Visualize() {        super("Quadtree");        getContentPane().setBackground(Color.WHITE);        setSize(600, 600);        setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        setLocationRelativeTo(null);    }    void drawRectangles(Graphics g) {        Random r = new Random();        Graphics2D g2d = (Graphics2D) g;        for (int i = 0; i < 100; i++){            int x = r.nextInt(qt.boundary.x);            int y = r.nextInt(qt.boundary.y);            Point p = new Point(x, y);            qt.insert(p);            g2d.drawLine(x, y, x, y);        }        g2d.drawRect(qt.boundary.x, qt.boundary.y, qt.boundary.w, qt.boundary.h);        if (qt.divided){            qt.northeast.drawRectangles(g);            qt.northwest.drawRectangles(g);            qt.southeast.drawRectangles(g);            qt.southwest.drawRectangles(g);        }    }    public void paint(Graphics g) {        super.paint(g);        drawRectangles(g);    }}